import axios from "axios";
import { BASE_URL, PATH } from "./api";

export const getAllSuratTugas = async () => {
  console.log("api", BASE_URL.URL + PATH.SURAT_TUGAS_PERJADIN);
  await axios
    .get(BASE_URL.URL + PATH.SURAT_TUGAS_PERJADIN, {
      headers: {
        "Content-Type": "application/json",
      },
    })
    .then((res) => {
      const { status, data, message } = res.data;
      if (status === "success") {
        return data;
      }
    })
    .catch((err) => {
      return err.message;
    });
};
