import React from 'react'
import { Select, Input, Table, Tag, Space, Form, Descriptions, Button } from 'antd';
import { useState, useEffect } from 'react';
import { PlusCircleOutlined } from '@ant-design/icons';
import { useNavigate } from "react-router-dom";
import axios from "axios";

function CekRKA() {
    const { Option } = Select;
    const FormItem = Form.Item;

    const columns = [
        {
            title: 'No',
            dataIndex: 'no',
            key: 'no',
        },
        {
            title: 'Nomor RKA',
            dataIndex: 'no_rka',
            key: 'no_rka',
        },
        {
            title: 'Permintaan ke-',
            dataIndex: 'permintaan_ke',
            key: 'permintaan_ke',
        },
        {
            title: 'No Surat Tugas',
            dataIndex: 'no_surat_tugas',
            key: 'no_surat_tugas',
        },
        {
            title: 'Tanggal Surat',
            dataIndex: 'tanggal_surat',
            key: 'tanggal_surat',
        },
        {
            title: 'Jumlah Petugas',
            dataIndex: 'jumlah_petugas',
            key: 'jumlah_petugas',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
        },
        {
            title: 'Aksi',
            key: 'aksi',
            render: (text, record) => (
                <Space size="middle">
                    <Button> Edit </Button>
                    <Button  icon={<PlusCircleOutlined />} type="primary" >Petugas</Button>
                    <Button  type="primary">SPPD</Button>  
                </Space>
            ),
        },
    ];

    const data = [
        {
            no: '1',
            no_rka: 'A122334546',
            permintaan_ke: 1,
            no_surat_tugas: '1234/02/03/2022',
            tanggal_surat: '02/03/2022',
            jumlah_petugas: 1,
            status: 'TTE'

          },
    
    ];
    const [KodeRKA, setKodeRKA] = useState([]);
    useEffect(() => {
        axios.get('http://172.16.100.69:6000/rka')
            .then(response => response.data)
            .then((data) => {
                setKodeRKA(data);
                console.log(data);
            });

    }, []);
    // .then((data) => data.json())
    //     .then((res) => {
    //       setKodeRKA(res.data);
    //       console.log(res.data);
    //    })
    //    .catch((err) => console.log(err));
    return (

        <div className="p-5 bg-white mt-2">
            <h5 className="text-1xl font-bold centered "> Permintaan Kode RKA </h5>
            <Form
                layout='vertical'
                initialValues={{ remember: true }}
                //onFinish={postData}
                //onFinishFailed={() => alert('Isi Data Secara Lengkap')}
                autoComplete="off"
            >
                <Form.Item rules={[{ required: true, message: "Tidak boleh kosong!" }]}>
                    <Select placeholder="- Pilih Kode RKA -" style={{ width: 400 }}>
                        {KodeRKA.map((item, index) => (
                            <Option key={`rka-${index}`} value={item.kode_rka}>
                                {item.kode_rka}
                            </Option>
                        ))}
                    </Select>
                    <Button className="ml-2" type="primary">Request</Button>
                    <Button className="ml-2"> Refresh </Button>
                </Form.Item>
            </Form>
            <h5 className="text-1xl font-bold centered "> Daftar Permintaan </h5>
            <Table columns={columns} dataSource={data} />
        </div>

    )
}

export default CekRKA
