import { getAllSuratTugas } from "../../config/baseApi";
import { GET_PERJADIN } from "./type";

export const getSuratTugasAction = () => (dispatch) => {
  getAllSuratTugas()
    .then((res) => {
      dispatch({ type: GET_PERJADIN, payload: res });
    })
    .catch((err) => {
      console.log(err);
    });
};
