import React from "react";
import { Route, Routes, Link } from "react-router-dom";
import Login from "../pages/auth/Login";
import Register from "../pages/auth/Register";
import Dashboard from "../pages/dashboard";
import "../styles/global.css";

const App = () => {
  return (
    <div>
      <p>App</p>
    </div>
  );
};

const Home = () => {
  return (
    <Content>
      <div>
        <p>Home</p>
      </div>
    </Content>
  );
};

const NewTeam = () => {
  return (
    <Content>
      <div>
        <p>New team</p>
      </div>
    </Content>
  );
};

const UpdateTeam = () => {
  return (
    <Content>
      <div>
        <p>Update Team</p>
      </div>
    </Content>
  );
};

const Header = () => {
  return (
    <div>
      <p>Header</p>
    </div>
  );
};

const Sidebar = () => {
  return (
    <nav>
      <Link to="/">App</Link> | <Link to="home">Home</Link> |
      <Link to="/teams/new">create new</Link> |
      <Link to="/teams/update">Update</Link>
    </nav>
  );
};

const Footer = () => {
  return (
    <div>
      <p>Footer</p>
    </div>
  );
};

const Layout = ({ children }) => {
  // auth if user not login return to login page
  return <div>{children}</div>;
};

const Content = ({ children }) => {
  return <div className="mt-2">{children}</div>;
};

const Router = () => {
  return (
    <Routes>
      <Route exact path="/" element={<App />} />
      <Route exact path="/home" element={<Home />} />
      <Route exact path="/teams/new" element={<NewTeam />} />
      <Route exact path="/teams/update" element={<UpdateTeam />} />

      <Route path="*" element={<h1>Not found page</h1>} />
    </Routes>
  );
};

export default Router;
