const Home = () => {
  return (
    <div>
      <p>Home</p>
    </div>
  );
};

const About = () => {
  return (
    <div>
      <p>About</p>
    </div>
  );
};

const Contact = () => {
  return (
    <div>
      <p>Contact</p>
    </div>
  );
};

const Header = () => {
  return (
    <div>
      <p>Header</p>
    </div>
  );
};

const Sidebar = () => {
  return (
    <nav>
      <Link to="/">Home</Link> |<Link to="/about">About</Link> |
      <Link to="/contact">Contact</Link>
    </nav>
  );
};

const Footer = () => {
  return (
    <div>
      <p>Footer</p>
    </div>
  );
};

const Layout = ({ children }) => {
  // auth if user not login return to login page
  return (
    <div>
      <Header />
      <Sidebar />
      {/* bisa pake outlet buat ganti children */}
      <Outlet />
      <Footer />
    </div>
  );
};

const Router = () => {
  return (
    <Routes>
      <Route exact path="/" element={<Layout />}>
        <Route index element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/contact" element={<Contact />} />
      </Route>
      <Route path="*" element={<h1>Not found page</h1>} />
    </Routes>
  );
};

export default Router;
